// Resolve place slugs to Sanity document IDs
// This would typically call the sanityClient.resolvePlaceSlugs() function
// For n8n, we'll use an HTTP request to query Sanity

const placeSlugs = $input.item.json.body.placeSlugs || [];
const projectId = process.env.SANITY_PROJECT_ID || 'ow7iqhbw';
const dataset = process.env.SANITY_DATASET || 'production';

if (placeSlugs.length === 0) {
  return { json: { placeReferences: [] } };
}

// Build GROQ query
const slugsQuery = placeSlugs.map(slug => `"${slug}"`).join(', ');
const query = `*[_type == "place" && slug.current in [${slugsQuery}]]{_id, slug}`;
const encodedQuery = encodeURIComponent(query);
const queryUrl = `https://${projectId}.api.sanity.io/v2023-08-01/data/query/${dataset}?query=${encodedQuery}`;

// Return query URL and slugs for HTTP node to execute
return {
  json: {
    queryUrl,
    placeSlugs,
    topic: $input.item.json.body.topic
  }
};

// Transform Sanity query result into place references
const queryResult = $input.item.json.result || [];
const placeReferences = queryResult.map(place => ({
  _type: 'reference',
  _ref: place._id
}));

return {
  json: {
    placeReferences,
    topic: $input.first().json.topic,
    placeSlugs: $input.first().json.placeSlugs
  }
};

