// Parse LLM response and build Sanity mutation payload
const llmResponse = JSON.parse($input.item.json.choices[0].message.content);
const placeRefs = $input.first().json.placeReferences || [];

// Convert HTML content to simple portable text structure
// TODO: Implement proper HTML-to-portable-text conversion
const htmlContent = llmResponse.content || '';
const textContent = htmlContent.replace(/<[^>]+>/g, '').trim();
const paragraphs = textContent.split(/\n\s*\n/).filter(p => p.trim().length > 0);

const portableTextBlocks = paragraphs.map((text, index) => ({
  _type: 'block',
  _key: `block-${index}`,
  style: 'normal',
  children: [{
    _type: 'span',
    _key: `span-${index}`,
    text: text.trim(),
    marks: []
  }],
  markDefs: []
}));

// Build mutation payload
const mutation = {
  mutations: [{
    create: {
      _type: 'guide',
      title: llmResponse.title,
      slug: {
        _type: 'slug',
        current: llmResponse.slug || llmResponse.title.toLowerCase().replace(/[^\w\s-]/g, '').replace(/[\s_-]+/g, '-').replace(/^-+|-+$/g, '')
      },
      summary: llmResponse.summary,
      content: portableTextBlocks,
      guideType: llmResponse.guideType || 'list',
      ...(placeRefs.length > 0 && { places: placeRefs })
    }
  }]
};

return { json: mutation };
